update giscedata_bateria_virtual_polissa set data_final = null where id in (select bvc.id from giscedata_bateria_virtual_polissa bvc left join giscedata_polissa_modcontractual modcon on modcon.polissa_id = bvc.polissa_id and modcon.data_final = bvc.data_final left join giscedata_polissa pol on pol.id = bvc.polissa_id where bvc.data_final is not null and modcon.id is not null and pol.state = 'activa' and pol.autoconsumo = '00');