select pol.zona_carta, pol.enviament, count(*), string_agg(pol.name, ', ' order by pol.create_date asc), string_agg(to_char(pol.create_date,'YYYY-MM-DD'), ', ' order by pol.create_date asc) from hr_colaborador_giscedata_polissa colab_pol right join giscedata_polissa pol on pol.id = colab_pol.polissa_id where colaborador_id = 220 and zona_carta is null group by pol.enviament, pol.zona_carta order by pol.enviament;